/* This program computes powers of 10 exactly.
 * Used to generate postab[].
 */


#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define NDIGITS 4096

void times10(unsigned *a)
{
    int i;

    for (i = 0; i < NDIGITS; i++)
    {
        a[i] *= 10;
        if (i)
        {
            a[i] += a[i - 1] >> 8;
            a[i - 1] &= 0xFF;
        }
    }
}

void print(unsigned *a)
{
    int i;
    int p;
    int j;

    for (i = NDIGITS; i; )
    {
        --i;
        if (a[i])
            break;
    }

    printf("0x%x.", a[i]);
    p = i * 8;
    i--;
    for (j = 0; j < i; j++)
        if (a[j])
            break;
    for (; i >= j; i--)
    {
        printf("%02x", a[i]);
    }
    printf("p+%d", p);
}

